import { Text,View,Pressable,Image,StyleSheet} from "react-native";
import {Ionicons} from "@expo/vector-icons"

function Status(){
    return(
     <Pressable>
        <View style={StatusStyle.main}>
            <View>
                <Image style={StatusStyle.image} source={require("../Image/pp.jpg")}/>
                <Pressable>
                <View style={StatusStyle.camer}>
                <Ionicons name="add-circle" size={40}/>
                </View>
                </Pressable>
            </View>
            <View >
                <Text style={{fontWeight:"bold",marginBottom:2}}>My Status</Text>
            </View>
         </View>
     </Pressable>
    )
}


export default Status;


const StatusStyle=StyleSheet.create({
    main:{
      margin:10,
      flexDirection:"row",
      justifyContent:"space-between",
      borderBottomWidth:1,
      borderBottomColor:"#c3c3c3",
      alignItems:"center"
      
      
    },
    image:{
        width:80,
        height:80,
        borderRadius:40,
        backgroundColor:"orange",
        marginBottom:10
        
      },
      camer:{
        position:"relative",
        zIndex:10,
        top:-50,
        right:0
       

   }

})
