import { Text,View,FlatList,Modal,TextInput,StyleSheet,Button } from "react-native";
import ChatComp from "../Components/ChatComp";
import { useEffect ,useState} from "react";
import { GetData } from "../HttpsReq/HttpData";
function ChatOut({isSearch}){

    let nn;
     console.log(isSearch);
    if(isSearch){
      nn=isSearch;
    }else{
        nn=false;
    }

    const [inputData,setInputData]=useState([]);
    const [inpuText,setInpuText]=useState();

    function changeHandler(inpuText){
        setInpuText(inpuText);
    }

   useEffect(()=>{
        async function ggg(){
         const hh=await GetData();
         console.log(hh);
         setInputData(hh)
        }

        ggg();
      
   },[])

    function ourData(itemData){
        if(itemData.item.id.name && itemData.item.id.name!="null")
        {
           return <ChatComp name={itemData.item.id.name} imgUri={itemData.item.picture.medium}/>
        }
        
     }
    return(
        <>
         <Modal visible={nn} transparent={true}>
            <View style={ChatOutStyle.mainModal}>
                <View style={ChatOutStyle.innerContinder}> 
                    <Text style={ChatOutStyle.searchText}>Search</Text>
                    <View>
                    <TextInput style={ChatOutStyle.searchTextInput} onTextInput={changeHandler} value={inpuText}/>
                    <Button title="Cancel"/>
                    </View>
                    
                </View>
            </View>
        </Modal> 
        <FlatList data={inputData} renderItem={ourData} keyExtractor={(item)=>item.id.value} />
        </>
    )
}

export default ChatOut;

const ChatOutStyle=StyleSheet.create({
    mainModal:{
        flex: 1,
          flexDirection: 'column',
          justifyContent: 'flex-start',
          alignItems: 'flex-start',
    },
    innerContinder:{
        width: 380,
        height: 120,
        backgroundColor:"white",
        alignItems:"center"
    },
    searchText:{
        fontWeight:"bold",
        alignContent:"center"
        
    },
    searchTextInput:{
        paddingVertical:10,
        backgroundColor:"orange"
    }


})
