import { View,Modal ,Text,TextInput,Button,StyleSheet} from "react-native";
import { useNavigation } from "@react-navigation/native";
import { useState } from "react";

function ModalsCom({isVisible}){


    const [openClose,setOpenClose]=useState(false);

      if(isVisible===true){
        setOpenClose(true);
      }


    function cancelHandler(){
         setOpenClose(false);
    }
   
    return(

    <Modal animationType="slide" transparent={true}
       visible={openClose}  >

    
       <View style={ModalStyle.main}>
           <View style={ModalStyle.innerContinder}>
                <View style={ModalStyle.title}>
                  <Text>hiii</Text>
                </View>
                <View style={ModalStyle.content}>
                    <TextInput style={ModalStyle.input}/>
                </View>
                <View style={ModalStyle.buttons}>
                    <Button title="Save" color="#ADD8E6"/>
                    <Button title="cancel" color="#ADD8E6" onPress={cancelHandler}/>
                </View>
           </View>
       </View>
</Modal>

    )
}

export default ModalsCom;

const ModalStyle=StyleSheet.create({
    main:{
        flex: 1,
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
    },
    innerContinder:{
        padding:10,
        width: 300,
            height: 300,
            backgroundColor:"white",
            borderRadius:20

    },
    title:{
        backgroundColor:"#ADD8E6",
        padding:20,
        alignItems:"center",
        borderRadius:20
    },
    buttons:{
        flexDirection:"row",
        justifyContent:"space-between"
    },
    content:{
        flex:1
    },
    input:{
        borderBottomWidth:1,
        borderBottomColor:"#ADD8E6",
        padding:10

    }
})
