import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View } from 'react-native';
import{NavigationContainer} from '@react-navigation/native';
import { createMaterialTopTabNavigator } from '@react-navigation/material-top-tabs';
import { createStackNavigator } from '@react-navigation/stack';
import { createDrawerNavigator } from '@react-navigation/drawer';
import 'react-native-gesture-handler';
import {Dimensions} from 'react-native';
import Chats from './Screens/Chats';
import Updates from './Screens/Updates';
import { DrawerActions } from '@react-navigation/native';
import Calls from './Screens/Calls';
import {Ionicons} from '@expo/vector-icons';
import DrawerOption from './Screens/DrawerOption';

const Drawer=createDrawerNavigator();
const Top=createMaterialTopTabNavigator();
const Stack=createStackNavigator();

function TopOverView(){
  return(
       <Top.Navigator>
             <Top.Screen name='Chats'  component={Chats}></Top.Screen>
             <Top.Screen name='Updates' component={Updates}></Top.Screen>
             <Top.Screen name='Calls' component={Calls}></Top.Screen>
        </Top.Navigator>
  )
}

const SyncIconFunc = {
  drawerLabel: config=>
  <View style={{flexDirection:'row'}}>
  <Text style={{color:Colors.bg}}>
    Sync Data
  </Text>
  <Feather
          onPress={() => alert("bell pressed")}
          style={{ position:'absolute',right: 10}}
          size={24}
          color={Colors.bg}
          name="bell"
        />
  </View>
  };
function DrawerOverview(){
  return(
    <Drawer.Navigator  screenOptions={{ drawerPosition: 'left',
    drawerType:"back",
     drawerStyle: {
      width:"80%",
      backgroundColor:"orange",

    } }}>
        <Drawer.Screen name='drawer' options={SyncIconFunc}  component={DrawerOption}></Drawer.Screen>
    </Drawer.Navigator>

  )
}






export default function App() {

  return (
    <NavigationContainer>
         <Stack.Navigator id="LeftDrawer" screenOptions={({navigation})=>({

          headerRight:()=>
          <View style={styles.rightIcons}> 

             <Ionicons style={{marginRight:16}} name="camera" size={25} />
             <Ionicons style={{marginRight:16}} name="search" size={25}/>
             {/* <Ionicons style={{marginRight:16}} name="menu-outline" onPress={()=>navigation.navigate("DrawerOverview")} size={25}/>  */}
           </View> 

         })}>
                 <Stack.Screen name='DrawerOverview' options={{headerShown:false}} component={DrawerOverview}></Stack.Screen>

             <Stack.Screen name='WhatsApp' component={TopOverView}></Stack.Screen>

         </Stack.Navigator>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  rightIcons:{
   flexDirection:"row",
   justifyContent:"space-around",

  }
});
