import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View } from 'react-native';
import{NavigationContainer} from '@react-navigation/native';
import { createMaterialTopTabNavigator } from '@react-navigation/material-top-tabs';
import { createStackNavigator } from '@react-navigation/stack';
import { createDrawerNavigator ,DrawerToggleButton} from '@react-navigation/drawer';
import 'react-native-gesture-handler';
import {Dimensions} from 'react-native';
import Chats from './Screens/Chats';
import Updates from './Screens/Updates';
import { DrawerActions } from '@react-navigation/native';
import Calls from './Screens/Calls';
import {Ionicons} from '@expo/vector-icons';
import DrawerOption from './Screens/DrawerOption';

const Drawer=createDrawerNavigator();
const Top=createMaterialTopTabNavigator();
const Stack=createStackNavigator();

function TopOverView(){
  return(
       <Top.Navigator>
             <Top.Screen name='Chats'  component={Chats}></Top.Screen>
             <Top.Screen name='Updates' component={Updates}></Top.Screen>
             <Top.Screen name='Calls' component={Calls}></Top.Screen>
        </Top.Navigator>
  )
}

