//           style={{ position:'absolute',right: 10}}
//           size={24}
//           name="settings"
//         />
//   </View>
//   };
function DrawerOverview(){
  return(
    <Drawer.Navigator  screenOptions={{ drawerPosition: 'left',
    drawerType:"back",
    headerLeft:false,
    headerShown:false,
    headerRight:()=><DrawerToggleButton/>,
     drawerStyle: {
      width:"80%",
      backgroundColor:"orange",

    } }}>
        <Drawer.Screen name='drawer' component={DrawerOption}></Drawer.Screen>
    </Drawer.Navigator>

  )
}






export default function App() {

  return (
    <NavigationContainer>
         <Stack.Navigator id="LeftDrawer" screenOptions={({navigation})=>({

          headerRight:()=>
          <View style={styles.rightIcons}> 

             <Ionicons style={{marginRight:16}} name="camera" size={25} />
             <Ionicons style={{marginRight:16}} name="search" size={25}/>
             <Ionicons style={{marginRight:16}} name="menu-outline" onPress={()=>navigation.navigate("DrawerOverview")} size={25}/>  
           </View> 

         })}>

             <Stack.Screen name='WhatsApp' component={TopOverView}></Stack.Screen>
             <Stack.Screen name='DrawerOverview' options={{headerShown:false}} component={DrawerOverview}></Stack.Screen>


         </Stack.Navigator>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  rightIcons:{
   flexDirection:"row",
   justifyContent:"space-around",

  }
});
